import React from 'react';
import data from './data/scores.json' //The supplied data
import Table from './Table.js'

// Constants
const minimumSubs = 3;
const maximumSubs = 24;
const leaderBoardSize = 10;

function JsonProcessor() {
    var table = [];
    var player = [];

    for(var i = 0; i < data.length; i++){
      var name = data[i];
      if(name.submissions.length > minimumSubs) { // Ruling out submissions less than 3.
          var array = name.submissions;
          var scoreTotal = [];

          for(var j = 0; j < array.length; j++){
            var scores = array[j];
            scoreTotal.push(scores.score);
        }
        var entry = [];
        entry.push(name.name);
        entry.push(accumulateScores(scoreTotal));
        table.push(entry); // This is the data for the highscore table
      }
    }

      // Sorting table data in score order for easy display
      table.sort(function(a,b) {
        return b[1] - a[1];
      });

      // Reducing the size of our table to the most successful participants
      table.length = leaderBoardSize;

      // Calculating the positions of each entry
      table.push(calculatePostion(table));

  return (
    <div><Table data={table}/></div> // Rendering the table
  );
}

function accumulateScores(input){
  var sum = 0;
  var test = 0;

  // Sorting the scores so we can add up the best submissions
  input.sort(function(a,b) {
    return b - a;
  });

  for(var k = 0; k < input.length; k++){ //Adding up everything
    if(k <= maximumSubs - 1){ // Keeping sumissions to the best 24.
        sum += input[k];
        test ++;
      }
    }
    return(sum);
  }

// Ensuring that shared scores equal shared positions
function calculatePostion(arr) {
  var position = [];
  position.push(1);
  for(var m = 1; m < arr.length; m++){
    if(arr[m][1] == arr[m - 1][1]){
      position.push(position[m - 1])
    } else {
      position.push(position[m - 1] + 1)
    }
  }
      return(position);
}


export default JsonProcessor;
